var switcher = null;
var curSelected = 0;

function switchSpeakers () {
    if(curSelected == 1){
        setCurrentSpeaker(6);
    }else if(curSelected == 6){
        setCurrentSpeaker(1);
    }
}


function setCurrentSpeaker(who) {
    document.getElementById('speakers').setAttribute('data-highlighted', who || 0);

    curSelected= who || 0;

    window.clearTimeout(switcher);

    if(who == 1 || who == 6) { // andreas or brendan
        switcher = setTimeout(switchSpeakers, 7000);
    }
}

function askFor (){
    var who = prompt("Qual a ID do palestrante?");
    setCurrentSpeaker(who);
}

document.body.addEventListener('dblclick', askFor);
console.log(document.getElementById('askForBtn'), askFor);
document.getElementById('askForBtn').addEventListener('click', askFor);

document.body.addEventListener('keyup', function(event){
    document.querySelector('.info-intro').style.opacity = 0;
    if(event.keyCode == 27){
        setCurrentSpeaker(0);
    }
});
